import "./App.css";
import React from "react";

const colors = [
  "black",
  "green",
];

function App() {
  const [color, setColor] = React.useState(0);
  const [color1, setColor1] = React.useState(0);
  const [color2, setColor2] = React.useState(0);
  const [color3, setColor3] = React.useState(0);

  React.useEffect(() => {
    setInterval(() => {
      setColor(Math.floor(Math.random() * 10));
      setColor1(Math.floor(Math.random() * 10));
      setColor2(Math.floor(Math.random() * 10));
      setColor3(Math.floor(Math.random() * 10));
    }, 1000);
  }, []);

  return (
    <div className="App">
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gridTemplateRows: "1fr 1fr",
          width: "75vw",
          height: "75vw",
        }}
      >
        <div style={{ backgroundColor: colors[color] }}></div>
        <div style={{ backgroundColor: colors[color1] }}></div>
        <div style={{ backgroundColor: colors[color2] }}></div>
        <div style={{ backgroundColor: colors[color3] }}></div>
      </div>
    </div>
  );
}

export default App;
